import * as React from "react";
import Box from "@mui/material/Box";
import Typography from "@mui/material/Typography";
import Modal from "@mui/material/Modal";
import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";
import { Link } from "react-router-dom";
import styles from "./MainSt.module.css";

const style = {
  position: "absolute",
  top: "50%",
  left: "50%",
  transform: "translate(-50%, -50%)",
  width: 400,
  maxHeight: 600,
  bgcolor: "rgba(245, 245, 245)",
  border: "2px solid rgba(0, 0, 0, 0.1)",
  borderRadius: "16px",
  boxShadow: "0px 4px 20px rgba(0, 0, 0, 0.3)",
  p: 4,
  overflow: "auto",
};

interface ContentModalProps {
  type: "view" | "add";
  title?: string;
  content?: string;
  image?: string;
  onSave?: (data: { title: string, content: string, image?: File }) => void;
}

export default function ContentModal({
  type,
  title = "Varsayılan Başlık",
  content = "İçerik girilmedi.",
  image,
  onSave,
}: ContentModalProps) {
  const [open, setOpen] = React.useState(false);
  const [newTitle, setNewTitle] = React.useState(title);
  const [newContent, setNewContent] = React.useState(content);
  const [newImage, setNewImage] = (React.useState < File) | (null > null);

  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  const handleSave = () => {
    if (onSave) {
      onSave({
        title: newTitle,
        content: newContent,
        image: newImage,
      });
    }
    handleClose();
  };

  const handleImageChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files[0]) {
      setNewImage(e.target.files[0]);
    }
  };

  return (
    <div>
      {type === "view" ? (
        <Link className={styles.Link} onClick={handleOpen}>
          Devamını oku...
        </Link>
      ) : (
        <Button
          variant="contained"
          onClick={handleOpen}
          className={styles.Button}
        >
          Yeni İçerik Ekle
        </Button>
      )}

      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box sx={style}>
          {type === "view" ? (
            <>
              <Typography
                id="modal-modal-title"
                variant="h6"
                component="h2"
                sx={{ marginBottom: 2 }}
              >
                {title}
              </Typography>

              {image && (
                <img
                  src={
                    typeof image === "string"
                      ? image
                      : URL.createObjectURL(image)
                  }
                  alt="Modal görseli"
                  className={styles.Image2}
                  style={{
                    maxWidth: "100%",
                    height: "auto",
                    marginBottom: "16px",
                  }}
                />
              )}

              <Typography id="modal-modal-description" sx={{ mt: 2 }}>
                {content}
              </Typography>
            </>
          ) : (
            <Box
              component="form"
              sx={{
                display: "flex",
                flexDirection: "column",
                gap: 2,
              }}
            >
              <Typography variant="h6" component="h2">
                Yeni İçerik Ekle
              </Typography>

              <TextField
                label="Başlık"
                value={newTitle}
                onChange={(e) => setNewTitle(e.target.value)}
                fullWidth
              />

              <TextField
                label="İçerik"
                value={newContent}
                onChange={(e) => setNewContent(e.target.value)}
                multiline
                rows={4}
                fullWidth
              />

              <input
                type="file"
                accept="image/*"
                onChange={handleImageChange}
              />

              <Box
                sx={{
                  display: "flex",
                  justifyContent: "space-between",
                  marginTop: 2,
                }}
              >
                <Button variant="outlined" onClick={handleClose}>
                  İptal
                </Button>
                <Button variant="contained" onClick={handleSave}>
                  Kaydet
                </Button>
              </Box>
            </Box>
          )}
        </Box>
      </Modal>
    </div>
  );
}
