import React, { useState } from "react";
import Layout from "../../../components/layout";
import styles from "./MainSt.module.css";
import Image from "../../../assets/image/images.jpg";
import ContentModal from "./modal";

const NotificationsPage = () => {
  const [contents, setContents] = useState([
    {
      title: "Yapay Zekada Devrim",
      content: "Lorem ipsum...",
      image: Image,
      author: "kanfatih",
    },
    // Diğer içerikler...
  ]);

  const handleAddContent = (newContent) => {
    setContents([
      ...contents,
      {
        ...newContent,
        author: "kanfatih", // Varsayılan yazar
        image: newContent.image || Image, // Görsel verilmezse varsayılan görsel
      },
    ]);
  };

  return (
    <Layout>
      <div className={styles.DivBox}>
        <div className={styles.ContentBox}>
          {contents.map((content, index) => (
            <div key={index} className={styles.ContentDiv}>
              <div>
                <img className={styles.Image} src={content.image} alt="" />
              </div>
              <div className={styles.ContentHeader}>{content.title}</div>
              <div className={styles.ContentText}>
                {content.content.substring(0, 100)}...
              </div>
              <div className={styles.Link}>
                <ContentModal
                  type="view"
                  title={content.title}
                  content={content.content}
                  image={content.image}
                />
              </div>
              <div className={styles.UserName}>writtenBy:{content.author}</div>
            </div>
          ))}
        </div>
        <div>
          <ContentModal type="add" onSave={handleAddContent} />
        </div>
      </div>
    </Layout>
  );
};

export default NotificationsPage;
