import { Box, Button, TextField } from "@mui/material";
import React, { useState } from "react";
import Menu from "./components/Menu";
import İmageLogo from "../../../assets/image/logoTS.svg";
import styles from "./Sidebar.module.css";
import AvatarIcon from "./components/AvatarIcon.jsx";
import { Search } from "@mui/icons-material";
import SettingsApplicationsIcon from "@mui/icons-material/SettingsApplications";
import KeyboardDoubleArrowLeftIcon from "@mui/icons-material/KeyboardDoubleArrowLeft";
const Sidebar = () => {
  const [isOpen, SetİsOpen] = useState(true);
  const toggleSidebar = () => {
    SetİsOpen(!isOpen);
  };
  return (
    <>
      <button
        onClick={toggleSidebar}
        className="no-border"
        style={{
          border: "none",
          outline: "none",
          background: "transparent",
          cursor: "pointer",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          float: "right",
        }}
      >
        <KeyboardDoubleArrowLeftIcon />
      </button>

      <Box
        sx={{
          minWidth: "256px",
          height: "100vh",
          boxShadow: "0px 0px 6px #00000024",
          display: "flex",
          flexDirection: "column",
        }}
      >
        <div className={styles.logo}>
          <img src={İmageLogo} alt="" />
        </div>

        <div>
          <AvatarIcon />
        </div>

        <Box
          sx={{
            display: "flex",
            alignItems: "flex-end",
            justifyContent: "center",
            border: "1px solid #0000001F",
            margin: "12px 12px",

            borderRadius: "2px",
          }}
        >
          <Search sx={{ color: "action.active", mr: 1, my: 0.5 }} />
          <TextField id="search-input" label="Ara..." variant="standard" />
        </Box>

        <Menu />

        <Box
          sx={{
            display: "flex",
            justifyContent: "flex-end",
            margin: "0 12px",
          }}
        ></Box>

        <Box sx={{ flexGrow: 1 }}></Box>

        <Box
          sx={{
            padding: "16px",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <Button variant="outlined" startIcon={<SettingsApplicationsIcon />}>
            Ayarlar
          </Button>
        </Box>
      </Box>
    </>
  );
};

export default Sidebar;
