import React, { useState } from "react";
import Layout from "../../../components/layout";
import styles from "./MainSt.module.css";
import Image from "../../../assets/image/images.jpg";
import ContentModal from "./ContentModal";

const NotificationsPage = () => {
  const [contents, setContents] = useState([
    {
      title: "Yapay Zeka da Devrim",
      content: "Lorem ipsum dolor sit amet...",
      image: Image,
      author: "kanfatih",
    },
  ]);

  const handleAddContent = (newContent: {
    title: string,
    content: string,
    image?: string | File | null,
  }) => {
    // Güvenli ekleme
    if (newContent.title && newContent.content) {
      setContents([
        ...contents,
        {
          title: newContent.title,
          content: newContent.content,
          image: newContent.image || Image,
          author: "kanfatih",
        },
      ]);
    }
  };

  return (
    <Layout>
      <div className={styles.DivBox}>
        <div className={styles.ContentBox}>
          {contents.map((content, index) => (
            <div key={index} className={styles.ContentDiv}>
              <div>
                <img
                  className={styles.Image}
                  src={
                    typeof content.image === "string"
                      ? content.image
                      : URL.createObjectURL(content.image)
                  }
                  alt=""
                />
              </div>
              <div className={styles.ContentHeader}>{content.title}</div>
              <div className={styles.ContentText}>
                {content.content.substring(0, 100)}...
              </div>
              <div className={styles.Link}>
                <ContentModal
                  type="view"
                  title={content.title}
                  content={content.content}
                  image={content.image}
                />
              </div>
              <div className={styles.UserName}>writtenBy:{content.author}</div>
            </div>
          ))}
        </div>
        <div>
          <ContentModal type="add" onSave={handleAddContent} />
        </div>
      </div>
    </Layout>
  );
};

export default NotificationsPage;
