import React, { useState } from "react";
import Layout from "../../../components/layout";
import styles from "./MainSt.module.css";
import Content from "./components/Content";
import UIModal from "../../../components/ui/Modal";
import Button from "@mui/material/Button";
import CategoriesSelect from "../../../components/CatagoriesSelect/index";
import AddCircleIcon from "@mui/icons-material/AddCircle";
import { useEffect } from "react";
import toast from "react-hot-toast";
import { useParams } from "react-router-dom";
import AddPhotoAlternateIcon from "@mui/icons-material/AddPhotoAlternate";

const CategoriDetailPage = () => {
  let params = useParams();
  const [showModal, setShowModal] = useState(false);

  const [blogs, setBlogs] = useState([]);

  const handleOpenModal = () => setShowModal(true);
  const handleCloseModal = () => setShowModal(false);
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [image, setImage] = useState("");
  const [category, setCategory] = useState("");
  const token = localStorage.getItem("token");

  const handleSubmit = () => {
    console.log("Başlık:", title);
    console.log("İçerik:", content);
    console.log("Kategori:", category);

    const blogData = {
      title,
      content,
      category,
    };

    fetch("http://localhost:3000/blog", {
      headers: {
        "Content-Type": "application/json",
        Authorization: `${token}`,
      },
      body: JSON.stringify(blogData),
      method: "POST",
    })
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        toast.success(data.message);
        handleCloseModal();
        window.location.reload();
      })
      .catch((e) => {
        console.log(e);
      });
  };

  useEffect(() => {
    fetch(`http://localhost:3000/blog/category/${params.id}`, {
      headers: {
        "Content-Type": "application/json",
        Authorization: `${token}`,
      },
      method: "GET",
    })
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        setBlogs(data);
      })
      .catch((e) => {
        console.log(e);
      });
  }, [params.id]);

  console.log("blogs,", blogs);

  return (
    <Layout>
      <div className={styles.DivBox}>
        <div className={styles.ContentBox}>
          {blogs.length > 0 &&
            blogs?.map((blog, index) => <Content index={index} blog={blog} />)}
        </div>
        <div>
          <button onClick={handleOpenModal} className={styles.Button}>
            <AddCircleIcon style={{ fontSize: "60px" }} />
          </button>
          <UIModal open={showModal} onClose={handleCloseModal}>
            <div className={styles.UIModal}>
              <span>Birşeyler yaz </span>

              <input
                className={styles.AddInput}
                type="text"
                placeholder="  başlık"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
              />
              <input
                className={styles.AddInput}
                type="text"
                placeholder="  içerik"
                value={content}
                onChange={(e) => setContent(e.target.value)}
              />
              <input
                className={styles.AddInput}
                type="file"
                placeholder="  resim"
                title="fotoğraf"
                id="files"
                style={{ display: "none" }}
              />

              <label className={styles.AddInputImage} for="files">
                <AddPhotoAlternateIcon /> dosya seç
              </label>

              <CategoriesSelect value={category} onChange={setCategory} />

              <Button variant="contained" onClick={handleSubmit}>
                KAYDET
              </Button>
            </div>
          </UIModal>
        </div>
      </div>
    </Layout>
  );
};

export default CategoriDetailPage;
