import { useState } from "react";
import { Box, Button, TextField } from "@mui/material";
import Menu from "./components/Menu";
import İmageLogo from "../../../assets/image/logoTS.svg";
import styles from "./Sidebar.module.css";
import AvatarIcon from "./components/AvatarIcon.jsx";
import { Search } from "@mui/icons-material";
import SettingsApplicationsIcon from "@mui/icons-material/SettingsApplications";
import ArrowLeftIcon from "@mui/icons-material/ArrowLeft";
import KeyboardDoubleArrowLeftIcon from "@mui/icons-material/KeyboardDoubleArrowLeft";

const Sidebar = () => {
  const [isOpen, setIsOpen] = useState(true);

  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };

  return (
    <Box sx={{ display: "flex" }}>
      {/* Toggle Button */}
      <button
        onClick={toggleSidebar}
        style={{
          border: "none",
          outline: "none",
          background: "transparent",
          cursor: "pointer",
          position: "absolute",
          top: "16px",
          left: isOpen ? "260px" : "16px", // Açıldığında butonu biraz sağa kaydırır
          transition: "left 0.3s",
        }}
      >
        <KeyboardDoubleArrowLeftIcon />
      </button>

      {/* Sidebar */}
      {isOpen && (
        <Box
          sx={{
            minWidth: "256px",
            height: "100vh",
            boxShadow: "0px 0px 6px #00000024",
            display: "flex",
            flexDirection: "column",
            position: "relative",
            transition: "width 0.3s",
          }}
        >
          <div className={styles.logo}>
            <img src={İmageLogo} alt="" />
          </div>

          <div>
            <AvatarIcon />
          </div>

          <Box
            sx={{
              display: "flex",
              alignItems: "flex-end",
              justifyContent: "center",
              border: "1px solid #0000001F",
              margin: "12px 12px",
              borderRadius: "2px",
            }}
          >
            <Search sx={{ color: "action.active", mr: 1, my: 0.5 }} />
            <TextField id="search-input" label="Ara..." variant="standard" />
          </Box>

          <Menu />

          <Box
            sx={{
              display: "flex",
              justifyContent: "flex-end",
              margin: "0 12px",
            }}
          >
            <ArrowLeftIcon />
          </Box>

          <Box sx={{ flexGrow: 1 }}></Box>

          <Box
            sx={{
              padding: "16px",
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            <Button variant="outlined" startIcon={<SettingsApplicationsIcon />}>
              Ayarlar
            </Button>
          </Box>
        </Box>
      )}
    </Box>
  );
};

export default Sidebar;
