import React, { useState } from "react";
import Layout from "../../../components/layout";
import styles from "./MainSt.module.css";
import Content from "./components/Content";
import UIModal from "../../../components/ui/Modal";
import Button from "@mui/material/Button";
import CategoriesSelect from "../../../components/CatagoriesSelect/index";
import AddCircleIcon from "@mui/icons-material/AddCircle";
import { useEffect } from "react";

const NotificationsPage = () => {
  const [showModal, setShowModal] = useState(false);

  const handleOpenModal = () => setShowModal(true);
  const handleCloseModal = () => setShowModal(false);
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [image, setImage] = useState("");
  const [category, setCategory] = useState("");

  const handleSubmit = () => {
    console.log("Başlık:", title);
    console.log("İçerik:", content);
    console.log("Kategori:", category);
  };

  useEffect(() => {
    const token = localStorage.getItem("token");
    fetch("http://localhost:3000/blog/", {
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`, // Düzgün interpolasyon
      },
      method: "POST",
    })
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        console.log(data);
        setContent(data);
      })
      .catch((e) => {
        console.log(e);
      });
  }, []);

  return (
    <Layout>
      <div className={styles.DivBox}>
        <div className={styles.ContentBox}>
          <Content />
          <Content />
          <Content />
          <Content />
          <Content />
        </div>
        <div>
          <button onClick={handleOpenModal} className={styles.Button}>
            <AddCircleIcon style={{ fontSize: "60px" }} />
          </button>
          <UIModal open={showModal} onClose={handleCloseModal}>
            <div className={styles.UIModal}>
              <span>Birşeyler yaz </span>

              <input
                className={styles.AddInput}
                type="text"
                placeholder="  başlık"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
              />
              <input
                className={styles.AddInput}
                type="text"
                placeholder="  içerik"
                value={content}
                onChange={(e) => setContent(e.target.value)}
              />
              <input
                className={styles.AddInput}
                type="text"
                placeholder="  resim"
              />
              <CategoriesSelect
                value={category}
                onChange={(e) => setCategory(e.target.value)}
              />

              <Button variant="contained" onClick={handleSubmit}>
                KAYDET
              </Button>
            </div>
          </UIModal>
        </div>
      </div>
    </Layout>
  );
};

export default NotificationsPage;
